#!/usr/bin/env python3

#identify the percent of each base in a DNA sequence, determine the percentage of AT
dna= ''

dna_uppercase = dna.upper() 

percent_A = dna_uppercase.count('A')
percent_G = dna_uppercase.count('G')
percent_C = dna_uppercase.count('C')
percent_T = dna_uppercase.count('T')

print('%A', percent_A, '%G', percent_G, '%T', percent_T, '%C', percent_C)

#make into an RNA string
rna = dna_uppercase.replace('T', 'U')
print('RNA:', rna)

dna_seq2 = ''

dna_seq2_uppercase = dna_seq2.upper()

rna_seq2= dna_seq2_uppercase.replace('T', 'U')

print('RNA2:', rna_seq2)

