#dna = ''

dna =''

#testdna
#dna= 'AATTAGGGCC'

dna_uppercase = dna.upper()
dna_length = len(dna_uppercase)

percent_A = dna_uppercase.count('A')
percent_G = dna_uppercase.count('G')
percent_C = dna_uppercase.count('C')
percent_T = dna_uppercase.count('T')


dna_ATcontent = ((percent_A + percent_T)/dna_length)*100
dna_GCcontent = ((percent_G + percent_C)/dna_length)*100

print('length:', dna_length)
print('%AT:', dna_ATcontent)
print('%GC:', dna_GCcontent)

sub_dna=dna[99:201]

percent_Gsubdna = sub_dna.count('G')
print('number of G in bp100-200:',percent_Gsubdna)

